function setCurrentTrack (page, options) {
  const { application, setState, data, api } = options; 
  // api.Alert('setting track')

  const CLOUD_FRONT_URL = "https://s3.amazonaws.com/box.import/";

  const getPlayerURL = key => {
    const audioURL = `${CLOUD_FRONT_URL}${key}`
    .  replace("#", "%23")
      .replace(/\+/g, "%2B");
    return audioURL;
  }



  if (!data) return api.Alert('No data!!'); 
  // if (data.hasOwnProperty('cell')) return alert('Ignoring cell click')

  application.setState(state => {

    const fresh = {
      ...state,
      app_player_url: getPlayerURL(data.FileKey),
      current_id: data.ID,
      track: data.Title,
      album_image: data.albumImage,
      src: data.albumImage,
      artist: data.artistName,
      scrolling: data.Title?.length > 35,
      selected_index: data.row,
      selected_id: data.ID,
    }

    return fresh;
  });

  api.executeScriptByName('set hero information', {
    record: data 
  });

  setState(value => ({
    ...value, 
    selected_index: data.row,
    selected_id: data.ID,
    player_url: "https://s3.amazonaws.com/box.import/" + data.FileKey,
    track: data.Title,
    album_image: data.albumImage,
    src: data.albumImage,
    artist: data.artistName
  }));


}

