function listCellClick (page, options) {
  const { api, application, data } = options; 

  const { records } = data; 

  if (data.column === 'menu') {
    return api.executeScriptByName('open context menu', data)
  }

  if (data.column === 'favorite') {
    return api.executeScriptByName('add to playlist', data);
  } 

  if (data.column === 'Genre') {
    if (!data.Genre) return api.Alert("Unknown Genre")
    return api.openPath('genre', { genrekey: data.Genre})
  } 

  if (data.cell === 1) { 
    api.executeScriptByName('create play list', data);
    return api.executeScriptByName('play audio', data);
  }

  if (data.cell === 2) {
    if (!data.artistFk) return api.Alert("Unknown artist")
    return api.openPath('artist', { artist_id: data.artistFk})
  } 

  if (data.cell === 3) {
    if (data.albumFk) return api.openPath('album', { album_id: data.albumFk})
    else api.Alert ('Unknown album')
  } 

}

