async function transformSaveRequest (page, options) {
  const { application, api, data } = options; 

  let transformed = data; 

  const { playlist_file, playlist_name, playlist_full } = application.state;


  const targetList = playlist_full?.find(f => f.listKey === data.value);

  if (targetList) {
     const { related } = targetList;
     transformed = {
        ...targetList,
        related: related.indexOf(playlist_file) > -1
            ? related.filter(f => f !== playlist_file)
            : related.concat(playlist_file)
     } 
  }

  return transformed;


}

