function playNext (page, options) {
  const {application, api} = options;

  application.setState(state => {  
    api.executeScriptByName('play by index', { 
        row: state.selected_index - (-1) 
    })  
    return state;
  });

}

function playByIndex (page, options) {

  const {application, data, api} = options;

  application.setState(state => {
    const { play_list_items } = state; 
    const next_record = play_list_items[data.row];

    if (!next_record) {
       return api.Alert('Play queue ended', 'End of queue');
    }

    api.executeScriptByName('set current track', {
        ...next_record,
        row: data.row 
    }) ; 

    return state;
  });

  api.executeScriptByName('start player')

}

function playPrevious (page, options) {
   const { application, api } = options;

  application.setState(state => {  
    api.executeScriptByName('play by index', { 
        row: state.selected_index - 1 
    })  
    return state;
  });

}

function addToPlaylist (page, options) {
  const { data, application, setState } = options; 
    application.setState(state => {
      const { playlist_full } = state;
      if (!playlist_full) return alert('no records')

      const rows = playlist_full.map(item => ({
         text: item.Title,
         subtext: item.TrackCount + ' tracks',
         value: item.listKey,
         endIcon: item.related.indexOf(data.FileKey) > -1 ? 'Favorite' : 'FavoriteBorder'
      }));

      const list_marquee_scrolling = data.Title.length > 35;


      return {
         ...state,
         playlist_file: data.FileKey,
         playlist_name: data.Title,
         playlist_menu: rows,
         list_marquee_scrolling,
         drawer_open: true
      }

    })
}

function muteAudio (page, options) {
  const { api, application, setState } = options; 

  // start the player after a delay
  api.execRefByName('app_aux', player => {
     setTimeout (() => { 
       !player && alert('Could not find player!!')
        const muted = player.muted;
        player.muted = !muted;
        application.setState(s => ({
           ...s,
           volume_icon: muted ? 'VolumeUp' : 'VolumeOff'
         }))

     }, 888)
  }); 

}

function handlePlayClick (page, options) {
  const { application, setState, api } = options; 


  application.setState(state => {

    api.execRefByName('app_aux', player => {
      if (!player) return api.Alert('Player not found')
     // api.Alert(state?.is_paused.toString())
      if (state?.is_paused) return player.play() 
      player.pause();     
    }); 

    return state;
  });  


}

