function toggleMiniMode (page, options) {
  const { application, setState, api } = options; 

  application.setState(state => {
      const avatar_open = !state.avatar_open;

      // toggle state variables
      const settings = {
        avatar_open, 
        photo_open: !avatar_open,
        full_icon: !avatar_open ? "CloseFullscreen" : "OpenInFull"
      } 

      return {
        ...state,
        ...settings
      };
    });
}

